DECLARE SERIES INTEGER;
DECLARE DOCENTRY INTEGER;
DECLARE DOCNUM INTEGER;
DECLARE TARGETYPE INT;
DECLARE BASEENTRY INT;
DECLARE  DOCENTRYFAC VARCHAR(15);
DECLARE CODIGO VARCHAR(100);

SELECT (Select $["ODLN"."DocEntry"] FROM dummy) INTO DOCENTRY FROM dummy;
SELECT (Select $["ODLN"."DocNum"] FROM dummy) INTO DOCNUM FROM dummy;
SELECT (SELECT TOP 1 "DocEntry" from INV1 WHERE "BaseEntry"=:DOCENTRY and "BaseRef"=:DOCNUM )  INTO DOCENTRYFAC FROM dummy;
SELECT TO_INT((SELECT TOP 1 "TargetType" from DLN1 WHERE "DocEntry"=:DOCENTRY )) INTO TARGETYPE FROM dummy;
SELECT TO_INT((SELECT TOP 1 "BaseEntry" from DLN1 WHERE "DocEntry"=:DOCENTRY )) INTO BASEENTRY FROM dummy;

IF :TARGETYPE = 13 THEN
SELECT (SELECT "U_SER_EST" from OINV WHERE "DocEntry"=:DOCENTRYFAC ) INTO CODIGO  FROM dummy;
SELECT CODIGO FROM dummy;
ELSE
SELECT (SELECT "U_SER_EST" from OINV WHERE "DocEntry"=:BASEENTRY ) INTO CODIGO  FROM dummy;
SELECT CODIGO FROM dummy;
END IF;

