DECLARE DESCRIPCION VARCHAR(2);
DECLARE PRODUCTO VARCHAR(10);
DECLARE OBJETYPE INT;

select (SELECT $["ORIN"."DocType"] from dummy) into PRODUCTO from Dummy ;
IF PRODUCTO = 'S'
THEN
  SELECT (SELECT ($[$-39.25."Number"]) FROM DUMMY ) INTO  DOCENTRYFAC FROM DUMMY;
  SELECT TO_INT((select $[$-39.23."BaseType"] FROM DUMMY)) INTO OBJETYPE  FROM DUMMY;
END IF; 
IF PRODUCTO = 'I'
THEN
  SELECT (SELECT ($[$-38.45."Number"]) FROM DUMMY ) INTO  DOCENTRYFAC FROM DUMMY;
  SELECT TO_INT((select $[$-38.43."BaseType"] FROM DUMMY)) INTO OBJETYPE  FROM DUMMY;
END IF; 

IF :OBJETYPE  = 13 THEN
select (SELECT T0."U_SS_TipCom" FROM "OINV" T0 WHERE TO_VARCHAR(T0."DocEntry") = :DOCENTRYFAC ) into DESCRIPCION  from dummy;

END IF;

IF :OBJETYPE  = 203 THEN
select (SELECT T0."U_SS_TipCom" FROM "ODPI" T0 WHERE TO_VARCHAR(T0."DocEntry") = :DOCENTRYFAC ) into DESCRIPCION  from dummy;

END IF;

IF IFNULL(:DESCRIPCION,'')=''
THEN
  SELECT LEFT(T0."Code",2) AS "CÓDIGO",T0."Name"  AS "DESCRIPCIÓN" FROM "@SS_TIPCOMAUT"  T0  
  WHERE T0."Code" IN ('01_1','18','41_1');
END IF;

IF IfNULL(:DESCRIPCION,'')!=''
THEN
SELECT DESCRIPCION FROM DUMMY;
END IF;
